Scripts.OnClick:
if (this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end

if GF_ResultsListOffset > 0 then
	GF_ResultsListOffset = GF_ResultsListOffset - 20;
	GF_UpdateResults();
end

if getn(GF_FilteredResultsList) > GF_ResultsListOffset + 20 then
	GF_ResultsListOffset = GF_ResultsListOffset + 20;
	GF_UpdateResults();
end

if GF_BlackListOffset > 0 then
	GF_BlackListOffset = GF_BlackListOffset - 15;
	GF_UpdateBlackListItems();
end

local c = 0;
for k,v in pairs(GF_BlackList) do
	c = c+1
end
if c > GF_BlackListOffset + 15 then
	GF_BlackListOffset = GF_BlackListOffset + 15;
	GF_UpdateBlackListItems();
end

if (this:GetButtonState() == "PUSHED") then
	this.clickDelay = MESSAGE_SCROLLBUTTON_INITIAL_DELAY;
else
	PlaySound("igChatScrollDown");
	this:GetParent():ScrollDown();
end

if (this:GetButtonState() == "PUSHED") then
	this.clickDelay = MESSAGE_SCROLLBUTTON_INITIAL_DELAY;
else
	PlaySound("igChatScrollUp");
	this:GetParent():ScrollUp();
end
